
document.addEventListener('DOMContentLoaded',function(){const videoPlayers=document.querySelectorAll('video');let activeVideo=null;videoPlayers.forEach((videoPlayer,index)=>{const videoItem=videoPlayer.parentElement;videoPlayer.addEventListener('play',()=>{if(activeVideo&&activeVideo!==videoPlayer){activeVideo.pause();activeVideo.parentElement.classList.remove('active')}activeVideo=videoPlayer;videoItem.classList.add('active')});videoPlayer.addEventListener('pause',()=>{if(activeVideo===videoPlayer){activeVideo=null;videoItem.classList.remove('active')}});videoPlayer.addEventListener('ended',()=>{const nextVideo=videoPlayers[index+1];if(nextVideo){nextVideo.play();nextVideo.parentElement.classList.add('active');videoItem.classList.remove('active');activeVideo=nextVideo}});videoPlayer.addEventListener('click',()=>{activeVideo=videoPlayer})});document.addEventListener('keydown',function(event){if(event.code==='Space'&&activeVideo){event.preventDefault();if(activeVideo.paused){activeVideo.play()}else{activeVideo.pause()}}})});
